// if (type &amp;&amp; type.defaultProps) &#123;
//   const defaultProps = type.defaultProps;
//   for (propName in defaultProps) &#123;
//     if (props[propName] === undefined) &#123; // 排除了undefined
//       props[propName] = defaultProps[propName];
//     &#125;
//   &#125;
